function chunkBySize(arr, size = MAX_MESSAGE_COUNT) {
    arr = typeof arr === 'string' ? arr.split('\n') : arr
    const resultText = `\nã€æ¸©é¦¨æç¤ºã€‘ç‚¹å‡»é€šçŸ¥æŸ¥çœ‹å…¨éƒ¨`
    const operator = (t) => t.replace(/[0-9A-z]/g, (match) => ['ğŸ', 'ğŸ', 'ğŸ', 'ğŸ‘', 'ğŸ’', 'ğŸ“', 'ğŸ”', 'ğŸ•', 'ğŸ–', 'ğŸ—']?.[match] || match)
    const _arr = arr.map((it) => it.replace(/^(\d+)[ã€.]/, (match, p1) => `ã€${operator(p1)}ã€‘`))
    // .replace(/ã€å¾®è¯­ã€‘/, '[å¾®è¯­]')
    const subTitle = _arr[_arr.length - 1]
    let message = []
    if ($.isNode()) {
        message = _arr
    } else {
        for (const item of _arr) {
            if (message.join('\n').length >= size) {
                break
            }
            message.push(item)
        }
        message = [...message, resultText]
    }
    return {
        subTitle,
        message: message.join('\n').replace(/\n$/, '').replace(/^\n/, '')
    }
}
